mapper.select:
SELECT k.knowledge_id, k.name, k.type, k.file_size, k.mime_type, k.customer_id, c.company_name AS customer_name, k.summary, k.status, k.upload_user_id, u.realname AS upload_user_name, k.create_time FROM crm_knowledge k LEFT JOIN crm_customer c ON k.customer_id = c.customer_id LEFT JOIN manager_user u ON k.upload_user_id = u.user_id WHERE k.status != 2
AND (k.name LIKE CONCAT('%', #{query.keyword}, '%') OR k.content_text LIKE CONCAT('%', #{query.keyword}, '%'))
AND k.type = #{query.type}
AND k.customer_id = #{query.customerId}
AND k.upload_user_id = #{query.uploadUserId}
AND EXISTS (SELECT 1 FROM crm_knowledge_tag t WHERE t.knowledge_id = k.knowledge_id AND t.tag_name = #{query.tag})
ORDER BY k.create_time DESC
